import React from "react";
import "./Projects.css";
import Showscape from "./Showscape";

import Apple from '../img/Apple.png';
import AR from '../img/AR.png';
import AtlantisHub from '../img/Dusk.png';
import ShowscapeMain from '../img/ShowscapeMain.png';
import Moon from '../img/Moon.jpg';
import MembershipCard from '../img/MembershipCard_v2.jpg';

import PropTypes from 'prop-types';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { Button } from "@material-ui/core";

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`vertical-tabpanel-${index}`}
        aria-labelledby={`vertical-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box p={3}>
            {children}
          </Box>
        )}
      </div>
    );
  }

  TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
  };

  function a11yProps(index) {
    return {
      id: `vertical-tab-${index}`,
      'aria-controls': `vertical-tabpanel-${index}`,
    };
  }

  const StyledTabs = withStyles({
    indicator: {
      minHeight: '165px',
      maxHeight: '165px',
      marginTop: '10px',
      width: '1px',
      display: 'flex',
      alignItems: 'center',
      backgroundColor: '#f18c8e',
    }
  })((props) => <Tabs {...props} TabIndicatorProps={{}} />);

  const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      display: 'flex',
      width: "100%",
      height: '470px',
      margin: "1% 10%",
    },
    tab: {
        paddingRight: '30px',
        backgroundColor: 'rgb(249, 251, 255)',
        fontFamily: 'lato, sans-serif',
        padding: '0px 10px',
        textTransform: 'none',
        '&:hover': {
            backgroundColor: 'none'
        }

    },
    content: {
        display: 'flex',
        flexDirection: 'column',
        '& > img': {
            width: '200px',
            height: '110px',
            paddingTop: '20px'
        }
    }
  }));

const Projects = () => {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <StyledTabs
        orientation="vertical"
        variant="scrollable"
        value={value}
        onChange={handleChange}
      >
        <Tab {...a11yProps(0)} 
            component={()=> <Button onClick={()=> setValue(0)} className={classes.tab} >
                            <div className={classes.content}>
                                <img src={Apple} />
                                <p>siri</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Two" {...a11yProps(1)} 
            component={()=> <Button onClick={()=> setValue(1)} className={classes.tab}>
                            <div className={classes.content}>
                                <img src={AR}/>
                                <p>ar / vr</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Three" {...a11yProps(2)} 
            component={()=> <Button onClick={()=> setValue(2)} className={classes.tab} >
                            <div className={classes.content}>
                                <img src={AtlantisHub}/>
                                <p>daily renders</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Four" {...a11yProps(3)} 
            component={()=> <Button onClick={()=> setValue(3)} className={classes.tab} >
                            <div className={classes.content}>
                                <img src={ShowscapeMain}/>
                                <p>showscape</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Five" {...a11yProps(4)} 
            component={()=> <Button onClick={()=> setValue(4)} className={classes.tab} >
                            <div className={classes.content}>
                                <img src={AtlantisHub}/>
                                <p>atlantis hub</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Six" {...a11yProps(5)} 
            component={()=> <Button onClick={()=> setValue(5)} className={classes.tab} >
                            <div className={classes.content}>
                                <img src={Moon} />
                                <p>artwork</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Seven" {...a11yProps(6)}
            component={()=> <Button onClick={()=> setValue(6)} className={classes.tab} >
                            <div className={classes.content}>
                                <img src={MembershipCard}/>
                                <p>public relations</p>
                            </div>
                            </Button>}
        />
      </StyledTabs>
      <TabPanel value={value} index={0} >
        <div className="projectInfo"> 
            <p>Hi</p>
        </div>
      </TabPanel>
      <TabPanel value={value} index={1} >
        <div className="projectInfo"> 
            <p>1</p>
        </div>
      </TabPanel>
      <TabPanel value={value} index={2} >
        <div className="projectInfo"> 
            <p>2</p>
        </div>
      </TabPanel>
      <TabPanel value={value} index={3} >
        <div className="projectInfo"> 
          {Showscape}
        </div>
      </TabPanel>
      <TabPanel value={value} index={4} >
        <div className="projectInfo"> 
            <p>4</p>
        </div>
      </TabPanel>
      <TabPanel value={value} index={5} >
        <div className="projectInfo"> 
            <p>5</p>
        </div>
      </TabPanel>
      <TabPanel value={value} index={6} >
        <div className="projectInfo"> 
            <p>6</p>
        </div>
      </TabPanel>
    </div>
  );
}

  export default Projects;
