import React from "react";
import "./Projects.css";
import AtlantisHub from '../img/Day.png'

import PropTypes from 'prop-types';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { Button } from "@material-ui/core";

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`vertical-tabpanel-${index}`}
        aria-labelledby={`vertical-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box p={3}>
            <Typography>{children}</Typography>
          </Box>
        )}
      </div>
    );
  }

  TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
  };

  function a11yProps(index) {
    return {
      id: `vertical-tab-${index}`,
      'aria-controls': `vertical-tabpanel-${index}`,
    };
  }

  const StyledTabs = withStyles({
    indicator: {
      maxHeight: '100%',
      width: '3px',
      display: 'flex',
      alignItems: 'center',
      backgroundColor: '#f18c8e',
      filter: 'blur(4px)',
    }
  })((props) => <Tabs {...props} TabIndicatorProps={{}} />);

  const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      display: 'flex',
      width: "80%",
      height: '400px',
      margin: "3% 10%",
      '&:hover': {
        backgroundColor: 'transparent',
      }
    },
    tabs: {
      borderRight: `1px dotted #f0b7a4`,
      paddingLeft: '10px',
      fontFamily: 'lato, sans-serif',
    },
    tab: {
        display: 'flex',
        flexDirection: 'column',
    },
    imgTab: {
        width: '200px',
        paddingTop: '20px'
    }
  }));

const Projects = () => {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <StyledTabs
        orientation="vertical"
        variant="scrollable"
        value={value}
        onChange={handleChange}
        className={classes.tabs}
      >
        <Tab {...a11yProps(0)} 
            component={()=> <Button onClick={()=> setValue(0)} >
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        >
            <p>Atlantis Hub</p>
        </Tab>
        <Tab label="Item Two" {...a11yProps(1)} 
            component={()=> <Button onClick={()=> setValue(1)}>
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Three" {...a11yProps(2)} 
            component={()=> <Button onClick={()=> setValue(2)}>
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Four" {...a11yProps(3)} 
            component={()=> <Button onClick={()=> setValue(3)}>
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Five" {...a11yProps(4)} 
            component={()=> <Button onClick={()=> setValue(4)}>
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Six" {...a11yProps(5)} 
            component={()=> <Button onClick={()=> setValue(5)}>
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        />
        <Tab label="Item Seven" {...a11yProps(6)} 
            component={()=> <Button onClick={()=> setValue(6)}>
                            <div className={classes.tab}>
                                <img src={AtlantisHub} className={classes.imgTab}/>
                                <p>Atlantis Hub</p>
                            </div>
                            </Button>}
        />
      </StyledTabs>
      <TabPanel value={value} index={0}>
        Item One
      </TabPanel>
      <TabPanel value={value} index={1}>
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel>
      <TabPanel value={value} index={3}>
        Item Four
      </TabPanel>
      <TabPanel value={value} index={4}>
        Item Five
      </TabPanel>
      <TabPanel value={value} index={5}>
        Item Six
      </TabPanel>
      <TabPanel value={value} index={6}>
        Item Seven
      </TabPanel>
    </div>
  );
}

  export default Projects;
